   async function createJpgs(filePath,user){
//     return new  Promise((resolve,reject)=>{
        if(fs.existsSync(filePath)){
            let  files = fs.readdirSync(filePath)
             for(let i=0 ; i < files.length;i++){
                if(path.extname(files[i]) != ".jpg"){
                  let input = filePath + "/" + files[i]
                  let output = files[i].replace("'","")
                  console.log("File being considered for conversion",input)
                  const testFile = fs.readFileSync(input)
                  if(isPDF(testFile)){
                     await doActualConversion(input,output,filePath,user)
                     await new Promise(resolve => setTimeout(resolve,10000))
                  }
                }
             }
         }
   }
   let doActualConversion = function(input,output,filePath,user){
     return new Promise((resolve,reject)=>{
       if(user == req.user.user_id){	     
       console.log("In actual conversion file to convert is ",input)
        pdf2img.setOptions({
            type:'jpg',
            size:1024,
            density:600,
            outputdir:filePath + "/",
            outputname:output,
            page:null,
            quality:100
        });
        pdf2img.convert(input,function(err,info){
            if(err){
              reject(false)
              console.log('error covnerting',err)
//            allJpegs.push(filepath+"/"+outputname)
            }else{
//              resolve(true)
              allJpegs.push(filePath)
              console.log('converted successfully',info)
              resolve(true)
            }
        })
       }else{
	 resolve(true)      
       }       
//        resolve(true)
     })
   }




