    function convertToHexString(buffer){
       return Array.prototype.map.call(buffer.id, x => ('00' + x.toString(16)).slice(-2)).join('')
    }
    function getColorCodeName(colorCode){
         console.log("In the function getcolorCodeName()")
         for(let i=0; i < workerData.colorMasterDetails.length;i++){
            console.log("In color master ......checking for the colorCode sent " + colorCode)
            let colorMasterDetailsId =  Array.prototype.map.call(workerData.colorMasterDetails[i]._id.id, x => ('00' + x.toString(16)).slice(-2)).join('')              
            if(colorMasterDetailsId == colorCode.toString()){
               return workerData.colorMasterDetails[i].name
            }
         }
         return "In Progress"

    }

    function getColorCode(colorCode){
        for(let i=0; i < workerData.colorMasterDetails.length;i++){
           let colorMasterDetailsId =  Array.prototype.map.call(workerData.colorMasterDetails[i]._id.id, x => ('00' + x.toString(16)).slice(-2)).join('')
           if( colorMasterDetailsId  == colorCode.toString()){
               return workerData.colorMasterDetails[i].colorCode;
           }
        }
        return "#FFFBBBAAA";
    }





